$google = get_field('reviews_google', 'options');
$gis = get_field('reviews_2gis', 'options');
$prodoctorov = get_field('reviews_prodoctorov', 'options');

if(isset($args['full_block'])) {
	$full_block = $args['full_block'];
}else {$full_block = true;}

function feedback_links($yandex, $google, $gis, $prodoctorov){
	
	$feedback_posts = get_posts( array(
		'posts_per_page' => -1,
		// 'numberposts' => ($reviews_count) ? $reviews_count : 6,
		'category'    => 0,
		'orderby'     => 'date',
		'order'       => 'DESC',
		'post_type'   => 'post',
		'post_status' => 'publish',
		'suppress_filters' => true, // подавление работы фильтров изменения SQL запроса
	));
	global $post;
	$count_yandex = 0;
	$count_google = 0;
	$count_gis = 0;
	$count_prodoctorov = 0;
	foreach( $feedback_posts as $key => $post ){
		setup_postdata( $post );
		$id = get_the_id();
		$source_term = get_field('source', $id)['value'];
		if(!empty($source_term)) {
			if($source_term == 'yandex') {
				$count_yandex++;
			}if($source_term == 'google') {
				$count_google++;
			}if($source_term == 'gis') {
				$count_gis++;
			}if($source_term == 'prodoctors') {
				$count_prodoctorov++;
			}
		}
	}
	wp_reset_postdata();
	function inclination_count_feedback($value, $count) {
		$value = (int)$value;
		$count = (int)$count;
		if($count == 11) {
			$inclination = $count.' отзывов';
		}else {
			$inclination = ($value == 1) ? $count.' отзыв' : (($value >= 2 && $value < 5) ? $count.' отзыва' : $count.' отзывов');
		}
		if($count) {
			return $inclination;
		}else {
			return '';
		}
	}
